import React, { useState, useEffect, useRef } from 'react';
import '../../styles/MainPage/Speedometer.css';

const Speedometer = () => {
  const [speed, setSpeed] = useState(0); // в km/h
  const [isAnalog, setIsAnalog] = useState(true);
  const [unit, setUnit] = useState('kph');
  const intervalId = useRef(null);
  const prevPos = useRef(null);

  const getSpeedInUnit = () => {
    switch (unit) {
      case 'mps':
        return (speed / 3.6).toFixed(2); // km/h -> m/s
      case 'mph':
        return (speed * 0.621371).toFixed(2); // km/h -> mph
      default:
        return speed.toFixed(2);
    }
  };

  const accelerate = () => {
    setSpeed((prevSpeed) => Math.min(prevSpeed + 10, 330));
  };

  const decelerate = () => {
    setSpeed((prevSpeed) => Math.max(prevSpeed - 10, 0));
  };

  const toggleSpeedometerType = () => {
    setIsAnalog(!isAnalog);
  };

  const changeUnit = (newUnit) => {
    setUnit(newUnit);
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const toRad = (val) => (val * Math.PI) / 180;
    const R = 6371000; // радиус Земли в метрах
    const dLat = toRad(lat2 - lat1);
    const dLon = toRad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; // в метрах
  };

  const startTracking = () => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    if (intervalId.current !== null) {
      // Остановить трекинг
      clearInterval(intervalId.current);
      intervalId.current = null;
      setSpeed(0);
      prevPos.current = null;
      return;
    }

    // Первый вызов сразу
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        const currentTime = position.timestamp;

        if (prevPos.current) {
          const { latitude: prevLat, longitude: prevLon, time: prevTime } = prevPos.current;
          const dist = calculateDistance(prevLat, prevLon, latitude, longitude);
          const timeElapsed = (currentTime - prevTime) / 1000;

          if (timeElapsed > 0) {
            const speedMps = dist / timeElapsed;
            const speedKph = speedMps * 3.6;
            setSpeed(speedKph);
          }
        }

        prevPos.current = { latitude, longitude, time: currentTime };
      },
      (error) => {
        console.error('Error getting position:', error);
        alert('Ошибка получения геолокации');
      },
      { enableHighAccuracy: true }
    );

    intervalId.current = setInterval(() => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          const currentTime = position.timestamp;

          if (prevPos.current) {
            const { latitude: prevLat, longitude: prevLon, time: prevTime } = prevPos.current;
            const dist = calculateDistance(prevLat, prevLon, latitude, longitude);
            const timeElapsed = (currentTime - prevTime) / 1000;

            if (timeElapsed > 0) {
              const speedMps = dist / timeElapsed;
              const speedKph = speedMps * 3.6;
              setSpeed(speedKph);
            }
          }

          prevPos.current = { latitude, longitude, time: currentTime };
        },
        (error) => {
          console.error('Error getting position:', error);
        },
        { enableHighAccuracy: true }
      );
    }, 3000);
  };

  useEffect(() => {
    return () => {
      if (intervalId.current !== null) {
        clearInterval(intervalId.current);
      }
    };
  }, []);

  return (
    <div className="app-container">
      <main className="content">
        <div className="speedometer-section">
          <div className="toggle-buttons">
            <button onClick={toggleSpeedometerType}>
              {isAnalog ? 'Digital' : 'Analog'}
            </button>
          </div>

          <div className="speedometer-container">
            <div className="speedometer-unit-wrapper">
              <div className="speedometer-display">
                {isAnalog ? (
                  <div className="analog-speedometer">
                    <img
                      src="https://habrastorage.org/r/w1560/getpro/habr/post_images/c3c/9a8/456/c3c9a8456303a4f574b2b14dc4c8d499.png"
                      alt="speedometer dial"
                    />
                    <div
                      className="speedometer-needle"
                      style={{
                        transform: `rotate(${speed * 0.642857}deg)`,
                      }}
                    ></div>
                  </div>
                ) : (
                  <div className="digital-speedometer">
                    <p>
                      {getSpeedInUnit()} {unit}
                    </p>
                  </div>
                )}
              </div>
              <aside className="unit-selector">
                <button onClick={() => changeUnit('mps')}>m/s</button>
                <button onClick={() => changeUnit('mph')}>mph</button>
                <button onClick={() => changeUnit('kph')}>km/h</button>
              </aside>
            </div>
          </div>

          <div className="speed-controls">
            <div className="speed-control-buttons">
              <button onClick={accelerate}>Accelerate</button>
              <button onClick={decelerate}>Decelerate</button>
            </div>
            <button className="start-button" onClick={startTracking}>
              {intervalId.current === null ? 'Start' : 'Stop'}
            </button>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Speedometer;
